Q: String length in bytes in JavaScript A: <p><em>String <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/Symbol.iterator" rel="nofollow noreferrer">iterator</a>, iterates through the code point strings.</em></p>
<p><div class="snippet" data-lang="js" data-hide="false" data-console="true" data-babel="false" data-babel-preset-react="false" data-babel-preset-ts="false">
<div class="snippet-code">
<pre class="snippet-code-js lang-js prettyprint-override"><code>function sizeInUtf8 (str)
{
  let size = 0;
  for (let cpStr of str)
  {
    let cp = cpStr.codePointAt (0);
    size +=
      (cp &lt;    0x80 /*   128 */) ? 1 :
      (cp &lt;   0x800 /*  2048 */) ? 2 :
      (cp &lt; 0x10000 /* 65536 */) ? 3 : 4;
  }
  return size;
}

let noOfBytesAndStrArr = [
[ 2, '±',   2],
[ 3, '★',  3],
[ 4, '🦄', 4],
[15, 'ЭЭХ! Naïve?'],
[15, '汉字字符集'],
[64, '😀œ´®†¥¨ˆøπ¬˚∆˙©ƒ∂ßåΩ≈ç√∫˜µ≤i']];

for (let [expectedSize, str] of noOfBytesAndStrArr)
{
  let size = sizeInUtf8 (str);
  let passFail = expectedSize == size ? '✔': '✘';
  console.log (passFail, expectedSize, size, str);
}

