/* ------------------------- */

table {
  border: thin solid black;
  border-collapse: collapse;
}

table&gt;thead&gt;tr&gt;th,
table&gt;tbody&gt;tr&gt;td {
  padding: 5px;
  border: thin dotted gray;


}

table&gt;tbody:last-of-type&gt;tr:nth-last-child(1),
table&gt;tbody:last-of-type&gt;tr:nth-last-child(2),
table&gt;tbody:last-of-type&gt;tr:nth-last-child(3) {
  background-color: #fcba7a;
}

#gnucash_cmd_submit {
  display: none;
}</code></pre>
