class TimerReceiver : BroadcastReceiver() {
    override fun onReceive(context: Context, intent: Intent?) {

        val channelId = &quot;alert_channel&quot;
        val manager = context.getSystemService(NotificationManager::class.java)

        if (Build.VERSION.SDK_INT &gt;= Build.VERSION_CODES.O) {
            val channel = NotificationChannel(
                channelId,
                &quot;Timer Alert&quot;,
                NotificationManager.IMPORTANCE_HIGH
            )
            manager.createNotificationChannel(channel)
        }

        // Vibration pattern
        val vibration = longArrayOf(0, 500, 300, 500, 300)

        val notification = NotificationCompat.Builder(context, channelId)
            .setContentTitle(&quot;Time's Up!&quot;)
            .setContentText(&quot;Your timer has finished.&quot;)
            .setSmallIcon(R.drawable.ic_timer)
            .setPriority(NotificationCompat.PRIORITY_HIGH)
            .setVibrate(vibration)
            .setSound(Uri.parse(&quot;android.resource://${context.packageName}/raw/alarm_sound&quot;))
            .build()

        manager.notify(2001, notification)
    }
}

class TimerService : Service() {

    companion object {
        const val ACTION_TIMER_UPDATE = &quot;com.demo.background.froground.service.TIMER_UPDATE&quot;
    }

    private var totalTime = 0
    private var remainingTime = 0
    private var isPaused = false

    private val handler = Handler(Looper.getMainLooper())

    private val countdownRunnable = object : Runnable {
        override fun run() {
            if (!isPaused) {
                remainingTime--
                Log.d(&quot;TimerService&quot;, &quot;Tick, remainingTime = $remainingTime&quot;)

                updateNotification()
                sendTimeToUI()

                if (remainingTime &lt;= 0) {
                    Log.d(&quot;TimerService&quot;, &quot;Timer finished, calling finishTimer()&quot;)
                    finishTimer()
                    return
                }
            }
            handler.postDelayed(this, 1000)
        }
    }

    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {
        when (intent?.getStringExtra(&quot;action&quot;)) {
            &quot;start&quot; -&gt; {
                val duration = intent.getIntExtra(&quot;duration&quot;, 0)
                Log.d(&quot;TimerService&quot;, &quot;Start timer with $duration seconds&quot;)
                startTimer(duration)
            }
            &quot;pause&quot; -&gt; {
                isPaused = true
                Log.d(&quot;TimerService&quot;, &quot;Pause timer&quot;)
            }
            &quot;resume&quot; -&gt; {
                isPaused = false
                Log.d(&quot;TimerService&quot;, &quot;Resume timer&quot;)
            }
            &quot;stop&quot; -&gt; {
                Log.d(&quot;TimerService&quot;, &quot;Stop timer service&quot;)
                stopTimer()
            }
        }
        return START_NOT_STICKY
    }

    private fun stopTimer() {
        handler.removeCallbacks(countdownRunnable)  // STOP THREAD
        isPaused = false
        remainingTime = 0

        stopForeground(true)
        stopSelf()
    }

    private fun startTimer(seconds: Int) {
        totalTime = seconds
        remainingTime = seconds
        isPaused = false

        startForeground(1001, createNotification(&quot;Timer running&quot;))
        handler.post(countdownRunnable)
    }

    private fun updateNotification() {
        val text = formatTime(remainingTime)
        val notification = createNotification(text)
        val manager = getSystemService(NOTIFICATION_SERVICE) as NotificationManager
        manager.notify(1001, notification)
    }

    private fun sendTimeToUI() {
        val intent = Intent(ACTION_TIMER_UPDATE)
        intent.setPackage(packageName)
        val formatted = formatTime(remainingTime)
        intent.putExtra(&quot;time&quot;, formatted)
        Log.d(&quot;TimerService&quot;, &quot;Sending broadcast: $formatted&quot;)
        sendBroadcast(intent)
    }

    private fun finishTimer() {
        stopForeground(true)

        // If you have TimerReceiver, it can handle final alarm/notification
        val intent = Intent(this, TimerReceiver::class.java)
        intent.setPackage(packageName)
        sendBroadcast(intent)

        stopSelf()
    }

    private fun createNotification(text: String): Notification {
        val channelId = &quot;timer_channel&quot;

        val manager = getSystemService(NotificationManager::class.java)
        if (Build.VERSION.SDK_INT &gt;= Build.VERSION_CODES.O) {
            val channel = NotificationChannel(
                channelId, &quot;Timer Service&quot;,
                NotificationManager.IMPORTANCE_LOW
            )
            manager.createNotificationChannel(channel)
        }

        return NotificationCompat.Builder(this, channelId)
            .setContentTitle(&quot;Timer&quot;)
            .setContentText(text)
            .setSmallIcon(R.drawable.ic_timer)
            .setOngoing(true)
            .build()
    }

    private fun formatTime(sec: Int): String {
        val h = sec / 3600
        val m = (sec % 3600) / 60
        val s = sec % 60
        return String.format(&quot;%02d:%02d:%02d&quot;, h, m, s)
    }

    override fun onBind(intent: Intent?) = null
}



