Q: Read all contacts&#39; phone numbers in android A: <pre><code>// ------------------------- Contact.kt -------------------------
import androidx.room.Entity
import androidx.room.PrimaryKey

@Entity(tableName = &quot;contacts&quot;)
data class Contact(
    @PrimaryKey(autoGenerate = true) val id: Long = 0,
    var name: String,
    var phone: String,
    var email: String?
)

// ------------------------- ContactDao.kt -------------------------
import androidx.lifecycle.LiveData
import androidx.room.*

@Dao
interface ContactDao {
    @Query(&quot;SELECT * FROM contacts ORDER BY name ASC&quot;)
    fun getAll(): LiveData&lt;List&lt;Contact&gt;&gt;

    @Query(&quot;SELECT * FROM contacts WHERE id = :id LIMIT 1&quot;)
    suspend fun getById(id: Long): Contact?

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insert(contact: Contact): Long

    @Update
    suspend fun update(contact: Contact)

    @Delete
    suspend fun delete(contact: Contact)

    @Query(&quot;DELETE FROM contacts&quot;)
    suspend fun deleteAll()
}

// ------------------------- ContactDatabase.kt -------------------------
import android.content.Context
import androidx.room.Database
import androidx.room.Room
import androidx.room.RoomDatabase

@Database(entities = [Contact::class], version = 1, exportSchema = false)
abstract class ContactDatabase : RoomDatabase() {
    abstract fun contactDao(): ContactDao

    companion object {
        @Volatile private var INSTANCE: ContactDatabase? = null

        fun getInstance(context: Context): ContactDatabase =
            INSTANCE ?: synchronized(this) {
                Room.databaseBuilder(
                    context.applicationContext,
                    ContactDatabase::class.java,
                    &quot;contacts_db&quot;
                ).build().also { INSTANCE = it }
            }
    }
}

// ------------------------- ContactRepository.kt -------------------------
import androidx.lifecycle.LiveData

class ContactRepository(private val dao: ContactDao) {

    val allContacts: LiveData&lt;List&lt;Contact&gt;&gt; = dao.getAll()

    suspend fun insert(contact: Contact) = dao.insert(contact)
    suspend fun update(contact: Contact) = dao.update(contact)
    suspend fun delete(contact: Contact) = dao.delete(contact)
    suspend fun deleteAll() = dao.deleteAll()
    suspend fun getById(id: Long) = dao.getById(id)
}

// ------------------------- ContactViewModel.kt -------------------------
import android.app.Application
import androidx.lifecycle.*
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch

class ContactViewModel(application: Application) : AndroidViewModel(application) {

    private val repo: ContactRepository
    val contacts: LiveData&lt;List&lt;Contact&gt;&gt;

    init {
        val dao = ContactDatabase.getInstance(application).contactDao()
        repo = ContactRepository(dao)
        contacts = repo.allContacts
    }

    fun insert(contact: Contact) = viewModelScope.launch(Dispatchers.IO) {
        repo.insert(contact)
    }

    fun update(contact: Contact) = viewModelScope.launch(Dispatchers.IO) {
        repo.update(contact)
    }

    fun delete(contact: Contact) = viewModelScope.launch(Dispatchers.IO) {
        repo.delete(contact)
    }

    fun deleteAll() = viewModelScope.launch(Dispatchers.IO) {
        repo.deleteAll()
    }
}

// ------------------------- ContactsAdapter.kt -------------------------
import android.view.*
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView

class ContactsAdapter(
    private var items: List&lt;Contact&gt;,
    private val onClick: (Contact) -&gt; Unit
) : RecyclerView.Adapter&lt;ContactsAdapter.Holder&gt;() {

