Q: Google Apps Script how to get &quot;live&quot; data? A: <pre><code>function fetchStockPCR() {
  const ss = SpreadsheetApp.getActive();
  const listSheet = ss.getSheetByName(&quot;STOCK_LIST&quot;);
  const liveSheet = ss.getSheetByName(&quot;STOCK_PCR_LIVE&quot;);
  const histSheet = ss.getSheetByName(&quot;STOCK_PCR_HISTORY&quot;);

  const stocks = listSheet.getRange(2,1,listSheet.getLastRow()-1,2).getValues();
  liveSheet.getRange(&quot;A2:F100&quot;).clearContent();

  let row = 2;

  stocks.forEach(stock =&gt; {
    const symbol = stock[0];
    const enabled = stock[1];

    if (enabled !== &quot;YES&quot;) return;

    const url = `https://www.nseindia.com/api/option-chain-equities?symbol=${symbol}`;
    const options = {
      method: &quot;get&quot;,
      headers: {
        &quot;User-Agent&quot;: &quot;Mozilla/5.0&quot;,
        &quot;Accept-Language&quot;: &quot;en-US,en;q=0.9&quot;
      },
      muteHttpExceptions: true
    };

    try {
      const response = UrlFetchApp.fetch(url, options);
      const data = JSON.parse(response.getContentText());

      let ceVol = 0, peVol = 0;

      data.records.data.forEach(r =&gt; {
        if (r.CE) ceVol += r.CE.totalTradedVolume || 0;
        if (r.PE) peVol += r.PE.totalTradedVolume || 0;
      });

      const pcr = peVol / ceVol;
      let signal = &quot;⚪ RANGE&quot;;

      if (pcr &gt; 1.2) signal = &quot;🟢 BULLISH&quot;;
      if (pcr &lt; 0.8) signal = &quot;🔴 BEARISH&quot;;

      liveSheet.getRange(row,1,1,6)
        .setValues([[symbol, ceVol, peVol, pcr.toFixed(2), signal, new Date()]]);

      histSheet.appendRow([new Date(), symbol, pcr.toFixed(2)]);

      row++;

    } catch (e) {
      Logger.log(symbol + &quot; error&quot;);
    }
  });
}
</code></pre>

