import org.junit.jupiter.params.provider.MethodSource;

@ParameterizedClass
@MethodSource(&quot;foosToTest&quot;)
class FooInvariantsTest {

    static List&lt;Foo&gt; foosToTest() {
        return List.of(new CsvFoo(), new SqlFoo(), new XmlFoo());
    }

    @Parameter
    Foo fooToTest;

    @Test
    void testInvariant1() {
        // ...
    }

    @Test
    void testInvariant2() {
        // ...
    }

}
</code></pre>
