    //   mesh

    const boxHole = new THREE.CylinderGeometry( 15, 15, 51, 32, 32 );//BoxGeometry( 20, 20, 51 );  
    let matHole = new THREE.MeshPhongMaterial({ color: 0xaaaaaa });
